import pytest
from tinfoil.attestation.attestation import verify_sev_attestation

def test_sev_verify():
    att = {
        "format": "https://tinfoil.sh/predicate/sev-snp-guest/v1",
        "body": ""
    }

    verification = verify_sev_attestation(att["body"])

    # Expected certificate fingerprint
    expected_key_fp = "65083e5904c0273b644ad90e51e12a8d76fe07f2ab8ab14d76011b3e9c7dcaa7"

    # Assertions
    assert expected_key_fp == verification.public_key_fp
    assert 1 == len(verification.measurement.registers)
    assert "ff18f0a28cd150bb6123aa266adf07b4edac8ade942085f1b5283d37750ee924ac16199c2bfec7d42a6ab3964354685f" == verification.measurement.registers[0]

if __name__ == '__main__':
    pytest.main() 